package JavaQuestions;

public class CharisAlphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'X';
		if((c>= 'a' && c<='z') || (c>='A' && c<='Z')){
			System.out.println(c + " is an alphabet");
		}
		else{
			System.out.println(c + " is NOT an alphabet");

		}

	}

}
